function initPieChart(){const t=document.querySelector("#category");if(!t)return;const e=echarts.init(t),i=[];$(".census__basic-item.category ul li").each(function(){i.push({name:this.getAttribute("data-name"),value:this.getAttribute("data-value")})}),e.setOption({tooltip:{trigger:"item"},series:[{type:"pie",roseType:"area",itemStyle:{borderRadius:8},data:i}]})}document.addEventListener("DOMContentLoaded",()=>{initPieChart()});
